import "./register.css";
import axios from "axios";
import { useState } from "react";

export default function Register() {
  const [focused, setFocused] = useState(false);
  const handleFocus = (e) => {
    setFocused(true);
  };

  const [username, setUsername] = useState("");
  const [fullName, setFullName] = useState("");
  const [billingAddress, setBillingAddress] = useState("");
  const [mailingAddress, setMailingAddress] = useState("");
  const [preferSize, setPreferSize] = useState("");
  const [preferPayment, setPreferPayment] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    // setError(false);
    // try {
    //   const res = await axios.post("/auth/register", {
    //     username,
    //     fullName,
    //     billingAddress,
    //     mailingAddress,
    //     preferPayment,
    //     password,
    //   });
    //   res.data && window.location.replace("/login");
    // } catch (err) {
    //   setError(true);
    // }
  };

  return (
    <div className="register">
      <form className="registerForm" onSubmit={handleSubmit}>
        <h1 className="registerTitle">Register</h1>
        <div className="formInput">
          <label className="label">Username</label>
          <input
            className="registerInput"
            type="text"
            placeholder="Username"
            pattern="^[A-Za-z0-9]{3,16}$"
            required={true}
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            onBlur={handleFocus}
            focused={focused.toString()}
          />
          <span className="errMessage">
            Username should be 3-16 characters and shouldn't include any special
            characters!
          </span>
        </div>
        <div className="formInput">
          <label className="label">Full Name</label>
          <input
            className="registerInput"
            type="text"
            placeholder="Full Name"
            pattern="^[a-zA-Z'-'\s]{3,}$"
            required={true}
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
            onBlur={handleFocus}
            focused={focused.toString()}
          />
          <span className="errMessage">
            Full Name shouldn't include any numbers or special characters!
          </span>
        </div>
        <div className="formInput">
          <label className="label">Billing Address</label>
          <input
            className="registerInput"
            type="text"
            placeholder="Billing Address"
            pattern="^^[#.0-9a-zA-Z\s,-]+$"
            required={true}
            value={billingAddress}
            onChange={(e) => setBillingAddress(e.target.value)}
            onBlur={handleFocus}
            focused={focused.toString()}
          />
          <span className="errMessage">
            Biling Address should be included number, street name, city, state,
            and zip code!
          </span>
        </div>
        <div className="formInput">
          <label className="label">Mailing Address</label>
          <input
            className="registerInput"
            type="text"
            placeholder="Mailing Address"
            pattern="^^[#.0-9a-zA-Z\s,-]+$"
            required={true}
            value={mailingAddress}
            onChange={(e) => setMailingAddress(e.target.value)}
            onBlur={handleFocus}
            focused={focused.toString()}
            disabled={true}
          />
          <span className="errMessage">
            Mailing Address should be included number, street name, city, state,
            and zip code!
          </span>
        </div>
        {/* <label>Password</label>
        <input
          className="registerInput"
          type="password"
          placeholder="Enter your password..."
          onChange={(e) => setPassword(e.target.value)}
        /> */}
        <button className="registerButton" type="submit">
          Register
        </button>
      </form>
      {/* {error && (
        <span style={{ color: "red", marginTop: "10px" }}>
          Something went wrong!
        </span>
      )} */}
    </div>
  );
}
