import React, { useState, useEffect, useContext } from "react";
import { UncontrolledDropdown, DropdownToggle, DropdownItem } from "reactstrap";
import Table from "../../components/table/Table";
import { Context } from "../../context/Context";
import "./book.css";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { setHours, setMinutes } from "date-fns";
import StripeCheckout from "react-stripe-checkout";
import axios from "axios";

export default function Book() {
  const KEY = process.env.REACT_APP_STRIPE;

  const { user } = useContext(Context);

  const [error, setError] = useState(false);

  const [totalTables, setTotalTables] = useState([]);

  // Information about table and date
  const [selection, setSelection] = useState({
    table: {
      name: null,
      id: null,
    },
    datetime: null,
    size: null,
  });

  // Information about reservation's user
  const [booking, setBooking] = useState({
    userId: user ? user._id : null,
    name: "",
    phone: "",
    email: "",
  });

  const [stripeToken, setStripeToken] = useState(null);

  // Click and choose table
  const selectTable = (table_name, table_id) => {
    setSelection({
      ...selection,
      table: {
        name: table_name,
        id: table_id,
      },
    });
  };

  // Create size
  const getSizes = (_) => {
    let newSizes = [];
    for (let i = 1; i <= 8; i++) {
      newSizes.push(
        <DropdownItem
          key={i}
          className="bookingOptionItem"
          onClick={(e) => {
            let newSel = {
              ...selection,
              table: {
                ...selection.table,
              },
              size: i,
            };
            setSelection(newSel);
          }}
        >
          {i}
        </DropdownItem>
      );
    }
    return newSizes;
  };

  // Create tables
  const getTables = (_) => {
    console.log("Getting tables");
    if (getEmptyTables() > 0) {
      let tables = [];
      totalTables.forEach((table) => {
        if (table.isAvailable) {
          tables.push(
            <Table
              key={table._id}
              id={table._id}
              chairs={table.capacity}
              name={table.name}
              empty
              selectTable={selectTable}
            />
          );
        } else {
          tables.push(
            <Table
              key={table._id}
              id={table._id}
              chairs={table.capacity}
              name={table.name}
              selectTable={selectTable}
            />
          );
        }
      });
      return tables;
    }
  };

  // Get total number of available tables
  const getEmptyTables = (_) => {
    let tables = totalTables.filter((table) => table.isAvailable);
    return tables.length;
  };

  // Check if date is weekend or not
  const checkWeekend = (_) => {
    var currentDay = selection.datetime.getDay();
    var dateIsInWeekend = currentDay === 6 || currentDay === 0;
    if (dateIsInWeekend === true) {
      return true;
    } else {
      return false;
    }
  };

  // Date && Time
  const [startDate, setStartDate] = useState(null);
  const filterPassedTime = (time) => {
    const currentDate = new Date();
    const selectedDate = new Date(time);
    return currentDate.getTime() < selectedDate.getTime();
  };

  // Stripe method
  const onToken = (token) => {
    setStripeToken(token);
  };

  // API *** Send and Store Payment to database
  // eslint-disable-next-line react-hooks/exhaustive-deps
  const createPayment = async () => {
    try {
      const res = await axios.post("/payment", {
        userId: user ? user._id : null,
        amount: "$10",
        date: selection.datetime.toUTCString(),
        table: selection.table.name,
      });
      console.log(res.data);
    } catch (error) {
      console.log(error);
    }
  };

  // API *** Stripe Payment Checkout
  useEffect(() => {
    const makeRequest = async () => {
      try {
        const res = await axios.post("/checkout/payment", {
          tokenId: stripeToken.id,
          amount: 1000,
        });
        console.log(res.data);
        createPayment();
      } catch (error) {
        console.log(error);
      }
    };
    stripeToken && makeRequest();
  }, [createPayment, stripeToken]);

  // API *** Get availability datetime and table
  useEffect(() => {
    // Check availability of tables (datetime & size)
    if (selection.datetime && selection.size) {
      (async (_) => {
        let selectedDate = selection.datetime.toUTCString();
        // selectedDate = selectedDate.toISOString().split(".")[0];
        let res = await fetch("http://localhost:5000/api/availability", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            date: selectedDate,
          }),
        });
        res = await res.json();

        let tableSize = selection.size;
        if (tableSize % 2 === 1) {
          tableSize = tableSize + 1;
        }
        let tables = res.tables.filter((table) =>
          selection.size > 0 ? table.capacity === tableSize : true
        );
        setTotalTables(tables);
      })();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [selection.datetime, selection.size]);

  // API *** Store Reservation to database
  const reserve = async (e) => {
    if (
      (booking.name.length === 0) |
      (booking.phone.length === 0) |
      (booking.email.length === 0)
    ) {
      e.preventDefault();
      console.log("Incomplete Details");
      setError(true);
    } else {
      try {
        let selectedDate = selection.datetime.toUTCString();
        let res = await fetch("http://localhost:5000/api/reserve", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            ...booking,
            date: selectedDate,
            table: selection.table.id,
          }),
        });
        res = await res.text();
        console.log("Reserved: " + res);
        window.location.replace("/thankyou");
      } catch (err) {}
    }
  };

  return (
    <div className="booking">
      <div className="bookingTop">
        <span className="bookingTitle">
          {!selection.table.id ? "Available Tables" : "Confirm Reservation"}
        </span>
        <span className="bookingTableName">
          {selection.table.id
            ? "You are booking " +
              selection.table.name +
              " at " +
              selection.datetime.toLocaleString("en-US")
            : null}
        </span>

        {error && (
          <span className="reservation-error">
            * Please fill out all of the details.
          </span>
        )}
      </div>

      {!selection.table.id ? (
        <div id="reservation-stuff" className="bookingPage">
          <div className="bookingItems">
            <div className="bookingItemEach">
              <DatePicker
                className="bookingOption"
                placeholderText="Select a Date and Time"
                selected={startDate}
                onChange={(e) => {
                  setStartDate(e);
                  selection.datetime = e;
                }}
                showTimeSelect
                timeIntervals={60}
                minDate={new Date()}
                minTime={setHours(setMinutes(new Date(), 0), 16)}
                maxTime={setHours(setMinutes(new Date(), 0), 23)}
                filterTime={filterPassedTime}
                dateFormat="MMMM d, yyyy h:mm aa"
              />
            </div>
            <div className="bookingItemEach">
              <UncontrolledDropdown>
                <DropdownToggle className="bookingOption">
                  {selection.size === null
                    ? "Select a Party Size"
                    : "Selected Size: " + selection.size.toString()}
                </DropdownToggle>
                <div className="bookingOptionMenu">{getSizes()}</div>
              </UncontrolledDropdown>
            </div>
          </div>
          <div className="tablesDisplay">
            <div>
              {getEmptyTables() > 0 ? (
                <div className="availableNumbers">
                  {getEmptyTables()} Available Tables
                </div>
              ) : null}

              {selection.datetime && selection.size ? (
                getEmptyTables() > 0 ? (
                  <div>
                    <div className="tableKey">
                      <span className="emptyTable"></span> &nbsp; Available
                      &nbsp;&nbsp;
                      <span className="fullTable"></span> &nbsp; Unavailable
                      &nbsp;&nbsp;
                    </div>
                    <div className="getTables">{getTables()}</div>
                  </div>
                ) : (
                  <div className="bookingTableMessage">No Available Tables</div>
                )
              ) : (
                <div className="bookingTableMessage">
                  Please select date, time, and size for your reservation.
                </div>
              )}
            </div>
          </div>
        </div>
      ) : (
        <div className="reservation">
          {checkWeekend() ? (
            <div>
              <span>
                Due to Weekend Reservation, We will charge $10 and Refund back
                to you if showup
              </span>

              <StripeCheckout
                name="DustinV Shop"
                image="https://avatars.githubusercontent.com/u/78432157?s=400&u=a3542854353b321e90e5f526d6ac7d5b30a702bf&v=4"
                billingAddress
                shippingAddress
                description={`Your Holding Charge is $10`}
                amount={1000}
                token={onToken}
                stripeKey={KEY}
              >
                <button className="reservationButton">PAY</button>
              </StripeCheckout>
            </div>
          ) : (
            <form className="reservationForm" onSubmit={reserve}>
              <label>Name</label>
              <input
                className="reservationInput"
                type="text"
                placeholder="Enter your name..."
                // value={booking.name}
                onChange={(e) => {
                  setBooking({
                    ...booking,
                    name: e.target.value,
                  });
                }}
              />
              <label>Email</label>
              <input
                className="reservationInput"
                type="text"
                placeholder="Enter your email..."
                // value={booking.email}
                onChange={(e) => {
                  setBooking({
                    ...booking,
                    email: e.target.value,
                  });
                }}
              />
              <label>Phone Number</label>
              <input
                className="reservationInput"
                type="text"
                placeholder="Enter your phone number..."
                // value={booking.phone}
                onChange={(e) => {
                  setBooking({
                    ...booking,
                    phone: e.target.value,
                  });
                }}
              />
              <button className="reservationButton" type="submit">
                Book Now
              </button>
            </form>
          )}
        </div>
      )}
    </div>
  );
}
