import React, { useState, useEffect, useContext } from "react";
import { UncontrolledDropdown, DropdownToggle, DropdownItem } from "reactstrap";
import Table from "../../components/table/Table";
import { Context } from "../../context/Context";
import "./book.css";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { setHours, setMinutes } from "date-fns";

export default function Book() {
  const { user } = useContext(Context);

  const [error, setError] = useState(false);

  const [totalTables, setTotalTables] = useState([]);

  // Information about table and date
  const [selection, setSelection] = useState({
    table: {
      name: null,
      id: null,
    },
    datetime: null,
    size: null,
  });

  // Information about reservation's user
  const [booking, setBooking] = useState({
    userId: user ? user._id : null,
    name: "",
    phone: "",
    email: "",
  });

  // const [times] = useState([
  //   "16:00",
  //   "17:00",
  //   "18:00",
  //   "19:00",
  //   "20:00",
  //   "21:00",
  //   "22:00",
  //   "23:00",
  // ]);

  // const getDate = (_) => {
  //   const months = [
  //     "January",
  //     "February",
  //     "March",
  //     "April",
  //     "May",
  //     "June",
  //     "July",
  //     "August",
  //     "September",
  //     "October",
  //     "November",
  //     "December",
  //   ];
  //   const date =
  //     months[selection.date.getMonth()] +
  //     " " +
  //     selection.date.getDate() +
  //     " " +
  //     selection.date.getFullYear();
  //   let time = selection.time.slice(0, -3);
  //   time = time + ":00";
  //   const datetime = new Date(date + " " + time);
  //   console.log(datetime);
  //   return datetime;
  // };

  const getEmptyTables = (_) => {
    let tables = totalTables.filter((table) => table.isAvailable);
    return tables.length;
  };

  useEffect(() => {
    // Check availability of tables (datetime & size)
    if (selection.datetime && selection.size) {
      (async (_) => {
        let selectedDate = selection.datetime.toISOString();
        let res = await fetch("http://localhost:5000/api/availability", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            date: selectedDate,
          }),
        });
        res = await res.json();

        let tables = res.tables.filter((table) =>
          selection.size > 0 ? table.capacity >= selection.size : true
        );
        setTotalTables(tables);
      })();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [selection.datetime, selection.size]);

  // Send and Store Reservation to database
  const reserve = async (e) => {
    e.preventDefault();
    console.log("Incomplete Details");
    setError(true);
    try {
      let selectedDate = selection.datetime.toISOString();
      console.log(selectedDate);
      let res = await fetch("http://localhost:5000/api/reserve", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          ...booking,
          date: selectedDate,
          table: selection.table.id,
        }),
      });
      res = await res.text();
      console.log("Reserved: " + res);
      window.location.replace("/thankyou");
    } catch (err) {}
  };

  // Click and choose table
  const selectTable = (table_name, table_id) => {
    setSelection({
      ...selection,
      table: {
        name: table_name,
        id: table_id,
      },
    });
  };

  // Create size
  const getSizes = (_) => {
    let newSizes = [];

    for (let i = 1; i <= 8; i++) {
      newSizes.push(
        <DropdownItem
          key={i}
          className="bookingOptionItem"
          onClick={(e) => {
            let newSel = {
              ...selection,
              table: {
                ...selection.table,
              },
              size: i,
            };
            setSelection(newSel);
          }}
        >
          {i}
        </DropdownItem>
      );
    }
    return newSizes;
  };

  // Create time
  // const getTimes = (_) => {
  //   let newTimes = [];
  //   times.forEach((time) => {
  //     newTimes.push(
  //       <DropdownItem
  //         key={time}
  //         className="bookingOptionItem"
  //         onClick={(_) => {
  //           let newSel = {
  //             ...selection,
  //             table: {
  //               ...selection.table,
  //             },
  //             time: time,
  //           };
  //           setSelection(newSel);
  //         }}
  //       >
  //         {time}
  //       </DropdownItem>
  //     );
  //   });
  //   return newTimes;
  // };

  // Create tables
  const getTables = (_) => {
    console.log("Getting tables");
    if (getEmptyTables() > 0) {
      let tables = [];
      totalTables.forEach((table) => {
        if (table.isAvailable) {
          tables.push(
            <Table
              key={table._id}
              id={table._id}
              chairs={table.capacity}
              name={table.name}
              empty
              selectTable={selectTable}
            />
          );
        } else {
          tables.push(
            <Table
              key={table._id}
              id={table._id}
              chairs={table.capacity}
              name={table.name}
              selectTable={selectTable}
            />
          );
        }
      });
      return tables;
    }
  };
  const [startDate, setStartDate] = useState(
    setHours(setMinutes(new Date(), 0), 17)
  );

  const filterPassedTime = (time) => {
    const currentDate = new Date();
    const selectedDate = new Date(time);
    // console.log(selection.datetime);
    return currentDate.getTime() < selectedDate.getTime();
  };

  return (
    <div className="booking">
      <div className="bookingTop">
        <span className="bookingTitle">
          {!selection.table.id ? "Available Tables" : "Confirm Reservation"}
        </span>
        <span className="bookingTableName">
          {selection.table.id
            ? "You are booking " +
              selection.table.name +
              " at " +
              selection.datetime
            : null}
        </span>

        {error ? (
          <span className="reservation-error">
            * Please fill out all of the details.
          </span>
        ) : null}
      </div>

      {!selection.table.id ? (
        <div id="reservation-stuff" className="bookingPage">
          <div className="bookingItems">
            <div className="bookingItemEach">
              <DatePicker
                className="bookingOption"
                selected={startDate}
                onChange={(e) => {
                  setStartDate(e);
                  selection.datetime = e;
                }}
                showTimeSelect
                timeIntervals={60}
                minDate={new Date()}
                minTime={setHours(setMinutes(new Date(), 0), 16)}
                maxTime={setHours(setMinutes(new Date(), 0), 23)}
                filterTime={filterPassedTime}
                dateFormat="MMMM d, yyyy h:mm aa"
                // value={selection.datetime}
                // onChange={(e) => {
                //   // if (!isNaN(new Date(new Date(e.target.value)))) {
                //   let newSel = {
                //     ...selection,
                //     table: {
                //       ...selection.table,
                //     },
                //     datetime: e,
                //   };
                //   setSelection(newSel);
                // }}
              />
              {/* <DatePicker
                className="bookingOption"
                selected={new Date()}
                minDate={new Date()}
                value={selection.date}
                onChange={(e) => {
                  // if (!isNaN(new Date(new Date(e.target.value)))) {
                  let newSel = {
                    ...selection,
                    table: {
                      ...selection.table,
                    },
                    date: e.target.value,
                  };
                  setSelection(newSel);
                  } else {
                    console.log("Invalid date");
                    let newSel = {
                      ...selection,
                      table: {
                        ...selection.table,
                      },
                      date: new Date(),
                    };
                    setSelection(newSel);
                  }
                }}
              /> */}
              {/* <input
                type="date"
                className="bookingOption"
                value={selection.date.toISOString().split("T")[0]}
                onChange={(e) => {
                  if (!isNaN(new Date(new Date(e.target.value)))) {
                    let newSel = {
                      ...selection,
                      table: {
                        ...selection.table,
                      },
                      date: new Date(e.target.value),
                    };
                    setSelection(newSel);
                  } else {
                    console.log("Invalid date");
                    let newSel = {
                      ...selection,
                      table: {
                        ...selection.table,
                      },
                      date: new Date(),
                    };
                    setSelection(newSel);
                  }
                }}
              ></input> */}
            </div>
            {/* <div className="bookingItemEach">
              <UncontrolledDropdown>
                <DropdownToggle color="none" caret className="bookingOption">
                  {selection.time === null ? "Select a Time" : selection.time}
                </DropdownToggle>
                <div className="bookingOptionMenu">{getTimes()}</div>
              </UncontrolledDropdown>
            </div> */}
            <div className="bookingItemEach">
              <UncontrolledDropdown>
                <DropdownToggle color="none" caret className="bookingOption">
                  {selection.size === null
                    ? "Select a Party Size"
                    : selection.size.toString()}
                </DropdownToggle>
                <div className="bookingOptionMenu">{getSizes()}</div>
              </UncontrolledDropdown>
            </div>
          </div>
          <div className="tablesDisplay">
            <div>
              {getEmptyTables() > 0 ? (
                <div className="availableNumbers">
                  {getEmptyTables()} Available Tables
                </div>
              ) : null}

              {selection.datetime && selection.size ? (
                getEmptyTables() > 0 ? (
                  <div>
                    <div className="tableKey">
                      <span className="emptyTable"></span> &nbsp; Available
                      &nbsp;&nbsp;
                      <span className="fullTable"></span> &nbsp; Unavailable
                      &nbsp;&nbsp;
                    </div>
                    <div className="getTables">{getTables()}</div>
                  </div>
                ) : (
                  <div className="bookingTableMessage">No Available Tables</div>
                )
              ) : (
                <div className="bookingTableMessage">
                  Please select date, time, and size for your reservation.
                </div>
              )}
            </div>
          </div>
        </div>
      ) : (
        <div className="reservation">
          <form className="reservationForm" onSubmit={reserve}>
            <label>Name</label>
            <input
              className="reservationInput"
              type="text"
              placeholder="Enter your name..."
              value={booking.name}
              onChange={(e) => {
                console.log(e.target.value);
                setBooking({
                  ...booking,
                  name: e.target.value,
                });
              }}
            />
            <label>Email</label>
            <input
              className="reservationInput"
              type="text"
              placeholder="Enter your email..."
              value={booking.email}
              onChange={(e) => {
                setBooking({
                  ...booking,
                  email: e.target.value,
                });
              }}
            />
            <label>Phone Number</label>
            <input
              className="reservationInput"
              type="text"
              placeholder="Enter your phone number..."
              value={booking.phone}
              onChange={(e) => {
                setBooking({
                  ...booking,
                  phone: e.target.value,
                });
              }}
            />
            <button className="reservationButton" type="submit">
              Book Now
            </button>
          </form>
        </div>
      )}
    </div>
  );
}
