import React, { useState, useEffect } from "react";
import { UncontrolledDropdown, DropdownToggle, DropdownItem } from "reactstrap";
import Table from "../../components/table/Table";
import "./book.css";

export default function Book() {
  const [totalTables, setTotalTables] = useState([]);

  // User's selections
  const [selection, setSelection] = useState({
    table: {
      name: null,
      id: null,
    },
    date: new Date(),
    time: null,
    size: null,
  });

  // User's booking details
  const [booking, setBooking] = useState({
    name: "",
    phone: "",
    email: "",
  });

  const [times] = useState([
    "4PM",
    "5PM",
    "6PM",
    "7PM",
    "8PM",
    "9PM",
    "10PM",
    "11PM",
  ]);
  // Basic reservation "validation"
  const [reservationError, setReservationError] = useState(false);

  const getDate = (_) => {
    const months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];
    const date =
      months[selection.date.getMonth()] +
      " " +
      selection.date.getDate() +
      " " +
      selection.date.getFullYear();
    let time = selection.time.slice(0, -2);
    console.log(time);
    time = selection.time > 12 ? time + 10 + ":00" : time + ":00";
    const datetime = new Date(date + " " + time);
    return datetime;
  };

  const getEmptyTables = (_) => {
    let tables = totalTables.filter((table) => table.isAvailable);
    return tables.length;
  };

  useEffect(() => {
    // Check availability of tables (time & date && size)
    if (selection.time && selection.date && selection.size) {
      (async (_) => {
        let datetime = getDate();
        let res = await fetch("http://localhost:5000/api/availability", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            date: datetime,
          }),
        });
        res = await res.json();
        // Filter available tables with location and group size criteria
        let tables = res.tables.filter((table) =>
          selection.size > 0 ? table.capacity >= selection.size : true
        );
        setTotalTables(tables);
      })();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [selection.time, selection.date, selection.size]);

  // Make the reservation if all details are filled out
  const reserve = async (e) => {
    if (
      (booking.name.length === 0) |
      (booking.phone.length === 0) |
      (booking.email.length === 0)
    ) {
      e.preventDefault();
      console.log("Incomplete Details");
      setReservationError(true);
    } else {
      const datetime = getDate();
      let res = await fetch("http://localhost:5000/api/reserve", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          ...booking,
          date: datetime,
          table: selection.table.id,
        }),
      });
      res = await res.text();
      console.log("Reserved: " + res);
    }
  };

  // Clicking on a table sets the selection state
  const selectTable = (table_name, table_id) => {
    setSelection({
      ...selection,
      table: {
        name: table_name,
        id: table_id,
      },
    });
  };

  // Generate party size dropdown
  const getSizes = (_) => {
    let newSizes = [];

    for (let i = 1; i <= 8; i++) {
      newSizes.push(
        <DropdownItem
          key={i}
          className="bookingOptionItem"
          onClick={(e) => {
            let newSel = {
              ...selection,
              table: {
                ...selection.table,
              },
              size: i,
            };
            setSelection(newSel);
          }}
        >
          {i}
        </DropdownItem>
      );
    }
    return newSizes;
  };

  // Generate locations dropdown
  const getTimes = (_) => {
    let newTimes = [];
    times.forEach((time) => {
      newTimes.push(
        <DropdownItem
          key={time}
          className="bookingOptionItem"
          onClick={(_) => {
            let newSel = {
              ...selection,
              table: {
                ...selection.table,
              },
              time: time,
            };
            setSelection(newSel);
          }}
        >
          {time}
        </DropdownItem>
      );
    });
    return newTimes;
  };

  // Generating tables from available tables state
  const getTables = (_) => {
    console.log("Getting tables");
    if (getEmptyTables() > 0) {
      let tables = [];
      totalTables.forEach((table) => {
        if (table.isAvailable) {
          tables.push(
            <Table
              key={table._id}
              id={table._id}
              chairs={table.capacity}
              name={table.name}
              empty
              selectTable={selectTable}
            />
          );
        } else {
          tables.push(
            <Table
              key={table._id}
              id={table._id}
              chairs={table.capacity}
              name={table.name}
              selectTable={selectTable}
            />
          );
        }
      });
      return tables;
    }
  };

  return (
    <div className="booking">
      <div className="bookingTop">
        <span className="bookingTitle">
          {!selection.table.id ? "Available Tables" : "Confirm Reservation"}
        </span>
        <span className="bookingTableName">
          {selection.table.id
            ? "You are booking " + selection.table.name
            : null}
        </span>

        {reservationError ? (
          <span className="reservation-error">
            * Please fill out all of the details.
          </span>
        ) : null}
      </div>

      {!selection.table.id ? (
        <div id="reservation-stuff" className="bookingPage">
          <div className="bookingItems">
            <div className="bookingItemEach">
              <input
                type="date"
                required="required"
                className="bookingOption"
                value={selection.date.toISOString().split("T")[0]}
                onChange={(e) => {
                  if (!isNaN(new Date(new Date(e.target.value)))) {
                    let newSel = {
                      ...selection,
                      table: {
                        ...selection.table,
                      },
                      date: new Date(e.target.value),
                    };
                    setSelection(newSel);
                  } else {
                    console.log("Invalid date");
                    let newSel = {
                      ...selection,
                      table: {
                        ...selection.table,
                      },
                      date: new Date(),
                    };
                    setSelection(newSel);
                  }
                }}
              ></input>
            </div>
            <div className="bookingItemEach">
              <UncontrolledDropdown>
                <DropdownToggle color="none" caret className="bookingOption">
                  {selection.time === null ? "Select a Time" : selection.time}
                </DropdownToggle>
                <div className="bookingOptionMenu">{getTimes()}</div>
              </UncontrolledDropdown>
            </div>
            <div className="bookingItemEach">
              <UncontrolledDropdown>
                <DropdownToggle color="none" caret className="bookingOption">
                  {selection.size === null
                    ? "Select a Party Size"
                    : selection.size.toString()}
                </DropdownToggle>
                <div className="bookingOptionMenu">{getSizes()}</div>
              </UncontrolledDropdown>
            </div>
          </div>
          <div className="tablesDisplay">
            <div>
              {getEmptyTables() > 0 ? (
                <div className="availableNumbers">
                  {getEmptyTables()} Available Tables
                </div>
              ) : null}

              {selection.date && selection.time && selection.size ? (
                getEmptyTables() > 0 ? (
                  <div>
                    <div className="tableKey">
                      <span className="emptyTable"></span> &nbsp; Available
                      &nbsp;&nbsp;
                      <span className="fullTable"></span> &nbsp; Unavailable
                      &nbsp;&nbsp;
                    </div>
                    <div className="getTables">{getTables()}</div>
                  </div>
                ) : (
                  <div className="bookingTableMessage">No Available Tables</div>
                )
              ) : (
                <div className="bookingTableMessage">
                  Please select date, time, and size for your reservation.
                </div>
              )}
            </div>
          </div>
        </div>
      ) : (
        <div className="reservation">
          <form className="reservationForm" onSubmit={reserve}>
            <label>Name</label>
            <input
              className="reservationInput"
              type="text"
              placeholder="Enter your name..."
              value={booking.name}
              onChange={(e) => {
                setBooking({
                  ...booking,
                  name: e.target.value,
                });
              }}
            />
            <label>Email</label>
            <input
              className="reservationInput"
              type="text"
              placeholder="Enter your email..."
              value={booking.email}
              onChange={(e) => {
                setBooking({
                  ...booking,
                  email: e.target.value,
                });
              }}
            />
            <label>Phone Number</label>
            <input
              className="reservationInput"
              type="text"
              placeholder="Enter your phone number..."
              value={booking.phone}
              onChange={(e) => {
                setBooking({
                  ...booking,
                  phone: e.target.value,
                });
              }}
            />
            <button className="reservationButton" type="submit">
              Book Now
            </button>
          </form>
          {reservationError && (
            <span style={{ color: "red", marginTop: "10px" }}>
              Something went wrong!
            </span>
          )}
        </div>
      )}
    </div>
  );
}
