import "./table.css";

export default function Table(props) {
  const getRow1 = (_) => {
    let chairs = [];
    for (var i = 0; i < Math.ceil(props.chairs / 2); i++) {
      chairs.push(
        <span
          key={i}
          className={props.empty ? "emptyTable" : "fullTable"}
        ></span>
      );
    }
    return chairs;
  };
  const getRow2 = (_) => {
    let chairs2 = [];
    for (var i = 0; i < Math.floor(props.chairs / 2); i++) {
      chairs2.push(
        <span
          key={i}
          className={props.empty ? "emptyTable" : "fullTable"}
        ></span>
      );
    }
    return chairs2;
  };

  return (
    <div className="tableContainer">
      <div
        className={props.empty ? "table selectable-table" : "table"}
        onClick={(_) => {
          props.empty
            ? props.selectTable(props.name, props.id)
            : console.log("Tried to select a full table");
        }}
      >
        <div className="table-row">{getRow1()}</div>
        <div className="table-row">
          <div className="text-center">{getRow2()}</div>
        </div>

        <div className="text-center table-name">{props.name}</div>
      </div>
    </div>
  );
}
